/**
 * Abstract Measure class.
 */
export class Measure {
	/**
	 * Constructor, stores the monoid.
	 */
	constructor(monoid) {
		this.monoid = monoid;
	}

	/**
	 * Returns the zero measure according to the monoid.
	 */
	zero() {
		return this.monoid.zero();
	}

	/**
	 * Adds measures together using monoid#plus.
	 */
	plus(a, b, c = undefined) {
		if (c === undefined) return this.monoid.plus(a, b);

		return this.monoid.plus(a, this.monoid.plus(b, c));
	}

	/**
	 * Returns the measure of an element. Must be implemented by classes
	 * extending Measure
	 */
	measure(_element) {
		throw new Error('measure : not implemented');
	}
}

export class IntegerAdd {
	zero() {
		return 0;
	}

	plus(a, b) {
		return a + b;
	}
}

export class IntegerMax {
	zero() {
		return Number.NEGATIVE_INFINITY;
	}

	plus(a, b) {
		return Math.max(a, b);
	}
}

export class IntegerMul {
	zero() {
		return 1;
	}

	plus(a, b) {
		return a * b;
	}
}

export const NoKey = {};

export class KeyMonoid {
	zero() {
		return NoKey;
	}

	plus(a, b) {
		return b === NoKey ? a : b;
	}
}

export class Product {
	constructor(a, b) {
		this.a = a;
		this.b = b;
	}

	zero() {
		return [this.a.zero(), this.b.zero()];
	}

	/**
	 * @param first
	 * @param second
	 */
	plus([a, b], [A, B]) {
		return [this.a.plus(a, A), this.b.plus(b, B)];
	}
}

export function product(a, b) {
	return new Product(a, b);
}

export * from './IntegerAdd.js';
export * from './IntegerMax.js';
export * from './IntegerMul.js';
export * from './KeyMonoid.js';
export * from './Product.js';

import {Measure} from '../0-base/index.js';
import {Monoids} from '../../1-api/0-Monoids.js';

export class Counter extends Measure {
	constructor() {
		super(Monoids.INTEGER_ADD);
	}

	measure(_element) {
		return 1;
	}
}

import {Measure} from '../0-base/index.js';
import {Monoids} from '../../1-api/0-Monoids.js';

export class Interval extends Measure {
	constructor() {
		super(Monoids.KEY_MAX);
	}

	measure(element) {
		return element;
	}
}

import {Measure} from '../0-base/index.js';
import {Monoids} from '../../1-api/0-Monoids.js';

export class KeyLen extends Measure {
	constructor() {
		super(Monoids.KEY_ADD);
	}

	measure(element) {
		return [element, 1];
	}
}

import {Measure} from '../0-base/index.js';
import {Monoids} from '../../1-api/0-Monoids.js';

export class KeyMeasure extends Measure {
	constructor() {
		super(Monoids.KEY);
	}

	measure(element) {
		return element;
	}
}

import {Measure} from '../0-base/index.js';
import {Monoids} from '../../1-api/0-Monoids.js';

export class Max extends Measure {
	constructor() {
		super(Monoids.INTEGER_MAX);
	}

	measure(element) {
		return element;
	}
}

export * from './Counter.js';
export * from './Interval.js';
export * from './KeyLen.js';
export * from './KeyMeasure.js';
export * from './Max.js';

export * from './0-base/index.js';
export * from './1-monoids/index.js';
export * from './2-derived/index.js';

import {
	IntegerAdd,
	IntegerMul,
	IntegerMax,
	KeyMonoid,
	product,
} from '../0-core/index.js';

const INTEGER_ADD = new IntegerAdd();
const INTEGER_MUL = new IntegerMul();
const INTEGER_MAX = new IntegerMax();
const KEY = new KeyMonoid();
const KEY_MAX = product(KEY, INTEGER_MAX);
const KEY_ADD = product(KEY, INTEGER_ADD);

export const Monoids = {
	INTEGER_ADD,
	INTEGER_MUL,
	INTEGER_MAX,
	KEY,
	KEY_MAX,
	KEY_ADD,
};

import {Counter, Max, KeyMeasure, KeyLen, Interval} from '../0-core/index.js';

const COUNTER = new Counter();
const MAX = new Max();
const KEY = new KeyMeasure();
const KEY_LEN = new KeyLen();
const INTERVAL = new Interval();

export const Measures = {
	COUNTER,
	MAX,
	KEY,
	KEY_LEN,
	INTERVAL,
	PRIO: MAX,
	SIZE: COUNTER,
	LEN: COUNTER,
};

